    //       onClearHandler();
    //       history.push("/");
    //     }
    //   });
  };

  const onClearHandler = () => {
    setFirstName("");
    setLastName("");
    setAge("");
    setEmail("");
    setPassword("");
    setPasswordConfirm("");
    setAddress("");
    setNumber("");
    setPhone("");
    setBirthday("");
    setSex("");
  };

  const sendEmail = () => {
    var randomPass = Math.floor(1000 + Math.random() * 9000);
    setConfirmPassword(randomPass);
    emailjs
      .send(
        "service_fhwf3t6",
        "template_vtomkos",
        {
          reply_to: email,
          name: firstName + " " + lastName,
          password: randomPass,
        },
        "IPOVb7KGIQxotCGCQ"
      )
      .then(function (res) {
        console.log(res.status);
        if (res.status === 200) {
          handleShow();
        } else {
          alert("Something Went Wrong!");
        }
      });
  };

  return (
    <div>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      <div className="loginCard">
        <div className="login-text">
          <h1>
            <BiRegistered />
          </h1>
          <p className="login-text-decoration">&nbsp;Register</p>
        </div>

        <hr />

        <Form>
          <Container>
            <Row>
              <Col xs={deviceSize < 900 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>First Name</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="First Name"
                    value={firstName}
                    onChange={(e) => {
                      setFirstName(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control
                    type="text"
                    value={lastName}
                    placeholder="Last Name"
                    onChange={(e) => {
                      setLastName(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={email}
                    placeholder="Email"
                    onChange={(e) => {
                      setEmail(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Phone</Form.Label>
                  <Form.Control
                    type="text"
                    value={phone}
                    placeholder="Phone"
                    onChange={(e) => {
                      setPhone(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    value={password}
                    placeholder="Email"
                    onChange={(e) => {
                      setPassword(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Comfirm Password</Form.Label>
                  <Form.Control
                    type="password"
                    value={passwordConfirm}
                    placeholder="Phone"
                    onChange={(e) => {
                      setPasswordConfirm(e.target.value);
                    }}
                  />
                </Form.Group>
                {password !== "" &&
                  passwordConfirm !== "" &&
                  password !== passwordConfirm && (
                    <p className="alertMessage">Passwords do not match!</p>
                  )}
              </Col>
            </Row>

            <Row>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Address</Form.Label>
                  <Form.Control
                    type="text"
                    value={address}
                    placeholder="Address"
                    onChange={(e) => {
                      setAddress(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Number</Form.Label>
                  <Form.Control
                    type="text"
                    value={number}
                    placeholder="Number"
                    onChange={(e) => {
                      setNumber(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Birthday</Form.Label>
                  <Form.Control
                    type="date"
                    value={birthday}
                    placeholder="Birthday"
                    onChange={(e) => {
                      setBirthday(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Sex</Form.Label>
                  <Form.Select
                    aria-label="Default select example"
                    onChange={(e) => {
                      setSex(e.target.value);
                      console.log(e.target.value);
                    }}
                  >
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                  </Form.Select>
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col xs={deviceSize < 700 ? 12 : 6}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                  <Form.Label>Age</Form.Label>
                  <Form.Control
                    type="number"
                    value={age}
                    placeholder="Age"
                    min="18"
                    onChange={(e) => {
                      setAge(e.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
            </Row>
            <Row>
              {succesfullyRegister && (
                <Alert variant="success">User Succefully Register!</Alert>
              )}
              {userAlreadyExist && (
                <Alert variant="danger">User Already Exist!</Alert>
              )}
            </Row>
          </Container>
          <Button
            variant="primary"
            disabled={isOkForRegister}
            onClick={onRegisterHandler}
          >
            Create acount
          </Button>{" "}
          &nbsp;&nbsp;&nbsp;
          <Button variant="warning" onClick={onClearHandler}>
            Clear
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default Register;
