import React from 'react'
import './NavBar.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faBars} from '@fortawesome/free-solid-svg-icons'
import { Link } from 'react-router-dom'
import { useState } from 'react'
import { useHistory ,useLocation } from 'react-router-dom';


const NavBar = () => {

  let history = useHistory();



  let login = false;
  if(sessionStorage.getItem('myData'))
  {
    login = true;
  }


  const [isLogin , setIsLogin] = useState(login)

  // --------------------NavBar Active By URL------------------

  var activeNavBar = [];
  const location = useLocation()

  if(location.pathname === '/' ||  location.pathname.includes('/seminar/') )
    {
      for (let i = 0; i < 4; i++) {
        activeNavBar[i] = ''   
      }
      activeNavBar[0] = 'active' ;

    }
    if(location.pathname.includes('/users') )
    {
      for (let i = 0; i < 4; i++) {
        activeNavBar[i] = ''   
      }
      activeNavBar[1] = 'active' ;

    }
    if(location.pathname.includes('/teams') )
    {
      for (let i = 0; i < 4; i++) {
        activeNavBar[i] = ''   
      }
      activeNavBar[2] = 'active' ;
    }
    if(location.pathname.includes('/seminartype') )
    {
      for (let i = 0; i < 4; i++) {
        activeNavBar[i] = ''   
      }
      activeNavBar[3] = 'active' ;
    }
  
  
  
  // -------------- Logout Function ----------------------
  const logout = () => {
    console.log(sessionStorage.getItem('myData'))
    sessionStorage.removeItem('myData');
    console.log(sessionStorage.getItem('myData'))
    
    window.location.reload(false);

    history.push("/");
  }

  




    //-------------------------- Responsive Bar Function ------------------------------
    function myFunction() {
        var x = document.getElementById("myTopnav");
        if (x.className === "topnav") {
          x.className += " responsive";
        } else {
          x.className = "topnav";
        }
      }

    return (
        
            <div>
                <div className="topnav" id="myTopnav">
                    
                    <Link className={activeNavBar[0]}  to="/" >Seminars</Link>
                    <Link className={activeNavBar[1]}   to="/users">Users</Link>
                    <Link  className={activeNavBar[2]}  to="/teams">Teams</Link>
                    <Link className={activeNavBar[3]}  to="/seminartype">Type</Link>
                    {isLogin && (
                    <button onClick={logout} >Logout</button>
                    )}
                    <a  className="icon" onClick={myFunction} >
                    <FontAwesomeIcon icon={faBars} />
                    </a>
                </div>


            </div>
        
    )
}

export default NavBar
