import React, { useEffect } from "react";
import {
  Container,
  Col,
  Row,
  Button,
  Modal,
  Alert,
  Form,
} from "react-bootstrap";
import { useState } from "react";
import "./HelpUs.css";
import { FaPen } from "react-icons/fa";

const HelpUs = () => {
  const [countries, setCountries] = useState([]);
  const [meals, setMeals] = useState([]);
  const [mealSelected, setMealSelected] = useState("");

  useEffect(() => {
    fetch(`https://www.themealdb.com/api/json/v1/1/list.php?a=list`)
      .then((response) => response.json())
      .then((data) => {
        var countriesData = [];
        data.meals.map((country) => {
          countriesData.push(country.strArea);
        });
        setCountries(countriesData);
      });
  }, []);

  const countrySelect = (country) => {
    fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${country}`)
      .then((response) => response.json())
      .then((data) => {
        console.log(data.meals);
        const mealsArray = [];
        data.meals.map((meal) => {
          mealsArray.push(meal);
        });
        setMeals(mealsArray);
      });
  };

  return (
    <div>
      <div className="title">
        <Row>
          <Col xs={9}>
            <h2>
              Your opinion matters &nbsp;
              <FaPen />
            </h2>
          </Col>
        </Row>

        <hr />
      </div>
      <div>
        <div className="helpUsTitle">
          <h5>&#8226; Big Categories</h5>
          <div className="helpUsTitleContent">
            <p>- Wine would you pair with : </p>
            <div className="helpUsTitleContent">
              <p>
                <strong>1.</strong> Red Meat{" "}
              </p>
              <Container>
                <Row>
                  <Col xs={3}>
                    <Form.Label>Type</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                  <Col xs={3}>
                    <Form.Label>Color</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                  <Col xs={3}>
                    <Form.Label>ra</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                  <Col xs={3}>
                    <Form.Label>Type</Form.Label>
                    <Form.Select size="sm">
                      <option>Small select</option>
                    </Form.Select>
                  </Col>
                 
                </Row>
              </Container>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HelpUs;
