import React from "react";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";

const Profile = () => {
  const [userDetails, setUserDetails] = useState([]);

  const cookies = new Cookies();
  var userEmail = cookies.get("email");

  // ------------ Get user Details -----------------
  const getUser = () => {
    fetch(`http://localhost:4000/getUser/${userEmail}`)
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setUserDetails([json]);
      });
  };
  useEffect(() => {
    getUser();
  }, []);

  return (
    <div>
      <div class="container rounded bg-white mt-5 mb-5">
        <div class="row">
          <div class="col-md-3 border-right">
            <div class="d-flex flex-column align-items-center text-center p-3 py-5">
              <img
                class="rounded-circle mt-5"
                width="150px"
                src="https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg"
              />
              <span class="font-weight-bold"></span>
              <span class="text-black-50"></span>
              <span> </span>
            </div>

            <div class="d-flex flex-column align-items-center text-center p-3 py-5">
              <img
                class="rounded-circle mt-5"
                width="150px"
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSNBSq3Ijqf7bxwcZxjzcgKm_RYhCWWhK7QTQ&usqp=CAU"
              />
              <span class="font-weight-bold"></span>
              <span class="text-black-50"></span>
              <span> </span>
            </div>
          </div>
          <div class="col-md-5 border-right">
            <div class="p-3 py-5">
              <div class="d-flex justify-content-between align-items-center mb-3">
                <h4 class="text-right">Profile Details</h4>
              </div>

              <div class="row mt-2">
                <div class="col-md-6">
                  <label class="labels">Name</label>
                  <h1 class="form-control"></h1>
                </div>
              </div>

              <div class="row mt-3">
                <div class="col-md-12">
                  <label class="labels">Mobile Number</label>
                  <h1 class="form-control"></h1>
                </div>
                <div class="col-md-12">
                  <label class="labels">Address</label>
                  <h1 class="form-control"></h1>
                </div>

                <div class="col-md-12">
                  <label class="labels">Birthday</label>
                  <h1 class="form-control"></h1>
                </div>
                <div class="col-md-12">
                  <label class="labels">Gender</label>
                  <h1 class="form-control"></h1>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
