import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import { useState, useEffect } from "react";
import { Button, Container, Row, Col, Form } from "react-bootstrap";
import "./Wines.css";

const Wines = () => {
  const [wines, setWines] = useState([]);

  const getWines = () => {
    fetch("http://localhost:4000/getWines")
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setWines(json);
      });
  };
  useEffect(() => {
    getWines();
  }, []);

  console.log(wines);

  return (
    <div>
      <Container className="item">
        <Row>
          {" "}
          <h5>{wines.length !== 0 && <p>{wines[0].WineName}</p>}</h5>{" "}
        </Row>
        <Row>
          <Col xs={2}></Col>\
          {wines.length !== 0 && (
            <img className="imageedit" src={wines[0].ImageUrl} alt="Avatar" />
          )}
        </Row>
      </Container>
    </div>
  );
};

export default Wines;
