import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import { useState , useEffect} from "react";

const Wines = () => {
  const [wines, setWines] = useState([]);
  const getWines = () => {
    fetch("http://localhost:4000/getWines")
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setWines([json]);
      });
  };


  useEffect(() => {
    getWines()
  }, []);

  const wineColumns = [
    {
      dataField: "_id",
      text: "Product ID",
    },
    {
      dataField: "WineName",
      text: "WineName",
    },
    {
      dataField: "Type",
      text: "Type",
    },
  ];
  return (
    <div>
      Wines
      {/* <BootstrapTable keyField="id" data={wineColumns} columns={wines} /> */}
    </div>
  );
};

export default Wines;
