import React from "react";
import { Button, Container, Row, Col, Form } from "react-bootstrap";
import { useState } from "react";
// import { useEffect } from "react";
import "./Login.css";
import Cookies from "universal-cookie";

const Login = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [testLogin, setTestLogin] = useState(true);

  const onTriger = (email) =>{
    props.handleCallback(email)
  }

  const isLogin = () => {
    let userDetails = {
      email: email,
      password: password,
    };
    fetch("http://localhost:4000/login", {
      method: "POST",
      body: JSON.stringify(userDetails),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((json) => {
        if (json.status === true) {
          
          setTestLogin(true);
          const cookies = new Cookies();
          cookies.set("email", json.email, { path: "/" });
          console.log(cookies.get("email"));
          onTriger(json.email)
          
        } else {
          alert("Error logim");
          setTestLogin(false);
        }
      });
  };

  return (
    <div>
      <div className="loginCard">
        <Container>
          <Row>
            <Col xs={6}>
              <p className="loginOrRegister">Login&nbsp;Form</p>
            </Col>
            <Col xs={4}></Col>
            <Col xs={2}></Col>
          </Row>
        </Container>

        <hr />

        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control
              type="email"
              value={email}
              placeholder="Enter email"
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
            {email.length > 0 && email.length < 5 && (
              <p className="alertMessage">
                Email must include at least 5 characters!
              </p>
            )}
            {!testLogin && (
              <p className="alertMessage">
                Email or Password are incorect!Try again!
              </p>
            )}
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Check me out" />
          </Form.Group>
          <Button
            variant="primary"
            onClick={isLogin}
            disabled={email === "" || password === "" || email.length < 5}
          >
            Submit
          </Button>
        </Form>
      </div>
      <div>
      <h2>Welcome to WineLovers website</h2>
<div class="container" id="container">

	<div class="form-container sign-up-container">
		<form action="/auth/register" method="POST">
			<h1>Create Account</h1>
			<span>Enter your details</span>
            
            
			<input type="text" placeholder="Name" name="name" required />
			<input type="email" placeholder="Email" name="email" required />
			<input type="password" placeholder="Password" name="password" required />
            <input type="password" placeholder="Confirm Password" name="confirnm_password" required />
            <input type="text" placeholder="Address with Number" name="address"  required />
            <input type="date" placeholder="Birthday" name="birthday" required />
            <input type="number" placeholder="Mobile" name="mobile" required />
            <select id="cars" name="gender" required >
                <option value="Male">Male</option>
                <option value="Female">Female</option>
                <option value="Other">Other</option>
                
            </select>

            
			<button type="submit" >Sign Up</button>
		</form>
	</div>

	<div class="form-container sign-in-container">
        
		<form  action="/auth/login" method="POST">
            

			<h1>Sign in</h1>
			<span>or use your account</span>
			<input type="email" placeholder="Email" name="email" required />
			<input type="password" placeholder="Password" name="password"  required />
			<a href="/forgotPassword">Forgot your password?</a>
			
			<button type="submit">Sign In</button>
		</form>
	</div>


	<div class="overlay-container">
		<div class="overlay">
			<div class="overlay-panel overlay-left">
				<h1>Welcome Back!</h1>
				<p>To keep connected with us please login with your personal info</p>
				<button class="ghost" id="signIn">Sign In</button>
			</div>
			<div class="overlay-panel overlay-right">
				<h1>Hello, Friend!</h1>
				<p>Enter your personal details and start journey with us</p>
				<button class="ghost" id="signUp">Sign Up</button>
			</div>
		</div>
	</div>
</div>
      </div>
    </div>
  );
};

export default Login;
