import React from "react";
import "./History.css";
import "./Card.css";
import { useState, useEffect } from "react";
import Cookies from "universal-cookie";
import { BiDetail } from "react-icons/bi";
import { dateFormater, addDays } from "./Functions/functions";
import { Table, Row, Col, Modal, Button, Badge } from "react-bootstrap";

const History = () => {
  const [history, setHistory] = useState([]);
  const [orderSelected, setOrderSelected] = useState([]);
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const getHistory = () => {
    fetch("http://localhost:4000/getHistory")
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        setHistory(json);
      });
  };

  useEffect(() => {
    getHistory();
  }, []);

  return (
    <div>
      <Modal show={show} onHide={handleClose} size="lg">
        <Modal.Body>
          {orderSelected.length !== 0 && (
            <>
              <Row>
                <Col>
                  <h4>Order Details</h4>
                  <br />
                </Col>
              </Row>
              <Row>
                <Col xs={6}>
                  Order Id : <strong>{orderSelected[0]._id}</strong>
                </Col>
                <Col xs={6}>
                  Order Date :{" "}
                  <strong>{orderSelected[0].OrderDate.slice(0, 10)}</strong>
                </Col>
              </Row>
              <Row>
                <Col xs={6}>
                  <hr />
                </Col>
                <Col xs={6}>
                  <hr />
                </Col>
              </Row>
              <Row>
                <Col xs={6}>
                  Total Price : <strong>{orderSelected[0].Price} â‚¬</strong>
                </Col>
                <Col xs={6}>
                  Shipping Date :{" "}
                  <strong>{orderSelected[0].OrderDate.slice(0, 10)}</strong>
                </Col>
              </Row>
              <Row>
                <Col xs={6}>
                  <hr />
                </Col>
                <Col xs={6}>
                  <hr />
                </Col>
              </Row>
              <Row>
                <Col xs={6}>
                  Order Send status{" "}
                  <Badge
                    pill
                    bg={orderSelected[0].HasBeenSend ? "success" : "danger"}
                  >
                    {orderSelected[0].HasBeenSend ? "Pending..." : "On Storage"}
                    &nbsp;
                  </Badge>
                </Col>
                <Col xs={6}>
                  Order Delivered  status{" "}
                  <Badge
                    pill
                    bg={orderSelected[0].HasBeenDelivered ? "success" : "danger"}
                  >
                    {orderSelected[0].HasBeenDelivered  ? "Delivered" : "No Delivered"}
                    &nbsp;
                  </Badge>
                </Col>
              </Row>
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Create Order Again
          </Button>
        </Modal.Footer>
      </Modal>
      <div className="title">
        <div class="container py-4">
          <header class="pb-3 mb-4 border-bottom">
            <a class="d-flex align-items-center text-dark text-decoration-none">
              <span class="fs-4">WineLovers Website - Previous Order</span>
            </a>
          </header>{" "}
        </div>
        {history.length !== 0 && (
          <>
            <Row>
              <Col md={{ span: 6, offset: 3 }}>
                <Table
                  striped
                  bordered
                  hover
                  size="sm"
                  className="personal-details-table"
                >
                  <thead>
                    <tr>
                      <th>Order Details</th>
                    </tr>
                  </thead>
                  <tbody>
                    {history.map((historyy) => (
                      <tr>
                        <td>
                          <Row>
                            <Col xs={4}>
                              Date:{" "}
                              <strong>{historyy.OrderDate.slice(0, 10)}</strong>
                            </Col>
                            <Col md={{ span: 1, offset: 6 }}>
                              <h4>
                                <BiDetail
                                  title="Order Details"
                                  className="order-details-icon"
                                  onClick={() => {
                                    handleShow();

                                    for (const key in history) {
                                      if (history[key]._id === historyy._id) {
                                        console.log(history[key]);
                                        setOrderSelected([history[key]]);
                                      }
                                    }
                                  }}
                                />
                              </h4>
                            </Col>
                          </Row>
                          <br />
                          <Row>
                            <Col xs={8}>
                              {historyy.Wines.map((wine) => (
                                <p>&#8226; {wine.wine.WineName}</p>
                              ))}
                            </Col>
                          </Row>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              </Col>
            </Row>
          </>
        )}
      </div>
    </div>
  );
};

export default History;
